package Codility;

public class ConcatenateString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Java and ";
		String st="Python language";
		System.out.println("Without using Function.......");
		System.out.println("Concatinate String without using function : "+s +st);
		String s3=s.concat(st);
		System.out.println("By using Function.......");
		System.out.println("Concatinate String by using function : "+s3);
	}

}
